     1→/**
     2→ * PCM-Ops Tools Theme Switcher
     3→ * 
     4→ * Handles light/dark theme switching with localStorage persistence
     5→ * Professional Fusion Dark Mode as default theme
     6→ */
     7→
     8→class ThemeSwitcher {
     9→    constructor() {
    10→        this.currentTheme = this.getStoredTheme() || 'dark'; // Default to dark mode
    11→        this.init();
    12→    }
    13→
    14→    init() {
    15→        this.applyTheme(this.currentTheme);
    16→        this.setupEventListeners();
    17→        this.updateThemeToggleIcon();
    18→    }
    19→
    20→    getStoredTheme() {
    21→        try {
    22→            return localStorage.getItem('pcm-ops-theme');
    23→        } catch (error) {
    24→            console.warn('localStorage not available, using default theme');
    25→            return null;
    26→        }
    27→    }
    28→
    29→    setStoredTheme(theme) {
    30→        try {
    31→            localStorage.setItem('pcm-ops-theme', theme);
    32→        } catch (error) {
    33→            console.warn('localStorage not available, theme preference not saved');
    34→        }
    35→    }
    36→
    37→    applyTheme(theme) {
    38→        document.documentElement.setAttribute('data-theme', theme);
    39→        this.currentTheme = theme;
    40→        this.setStoredTheme(theme);
    41→        
    42→        // Add fade-in animation to body for smooth transition
    43→        document.body.classList.add('fade-in-theme');
    44→        setTimeout(() => {
    45→            document.body.classList.remove('fade-in-theme');
    46→        }, 300);
    47→
    48→        // Dispatch custom event for other components to listen
    49→        document.dispatchEvent(new CustomEvent('themeChanged', {
    50→            detail: { theme }
    51→        }));
    52→    }
    53→
    54→    toggleTheme() {
    55→        const newTheme = this.currentTheme === 'dark' ? 'light' : 'dark';
    56→        this.applyTheme(newTheme);
    57→        this.updateThemeToggleIcon();
    58→    }
    59→
    60→    updateThemeToggleIcon() {
    61→        const toggleButton = document.getElementById('theme-toggle');
    62→        if (toggleButton) {
    63→            const icon = toggleButton.querySelector('#theme-icon');
    64→            const text = toggleButton.querySelector('.theme-text');
    65→            
    66→            if (icon && text) {
    67→                if (this.currentTheme === 'dark') {
    68→                    // Currently dark theme, show light mode option
    69→                    icon.className = 'bi bi-sun-fill';
    70→                    text.textContent = 'Light Mode';
    71→                    toggleButton.title = 'Switch to light mode';
    72→                    toggleButton.setAttribute('aria-label', 'Toggle theme, currently Dark Mode');
    73→                    toggleButton.setAttribute('aria-checked', 'true');
    74→                } else {
    75→                    // Currently light theme, show dark mode option
    76→                    icon.className = 'bi bi-moon-stars-fill';
    77→                    text.textContent = 'Dark Mode';
    78→                    toggleButton.title = 'Switch to dark mode';
    79→                    toggleButton.setAttribute('aria-label', 'Toggle theme, currently Light Mode');
    80→                    toggleButton.setAttribute('aria-checked', 'false');
    81→                }
    82→            }
    83→        }
    84→    }
    85→
    86→    setupEventListeners() {
    87→        // Theme toggle button click handler
    88→        document.addEventListener('click', (e) => {
    89→            if (e.target.closest('#theme-toggle')) {
    90→                e.preventDefault();
    91→                this.toggleTheme();
    92→            }
    93→        });
    94→
    95→        // Listen for system theme changes (optional feature)
    96→        if (window.matchMedia) {
    97→            const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
    98→            mediaQuery.addListener((e) => {
    99→                // Only auto-switch if user hasn't manually set a preference
   100→                if (!this.getStoredTheme()) {
   101→                    this.applyTheme(e.matches ? 'dark' : 'light');
   102→                    this.updateThemeToggleIcon();
   103→                }
   104→            });
   105→        }
   106→
   107→        // Keyboard shortcut (Ctrl/Cmd + Shift + T)
   108→        document.addEventListener('keydown', (e) => {
   109→            if ((e.ctrlKey || e.metaKey) && e.shiftKey && e.key === 'T') {
   110→                e.preventDefault();
   111→                this.toggleTheme();
   112→            }
   113→        });
   114→    }
   115→
   116→    getCurrentTheme() {
   117→        return this.currentTheme;
   118→    }
   119→
   120→    // Method to set theme programmatically
   121→    setTheme(theme) {
   122→        if (['light', 'dark'].includes(theme)) {
   123→            this.applyTheme(theme);
   124→            this.updateThemeToggleIcon();
   125→        }
   126→    }
   127→}
   128→
   129→// Theme-aware utility functions
   130→const ThemeUtils = {
   131→    // Get theme-appropriate color value
   132→    getThemeColor(lightColor, darkColor) {
   133→        const currentTheme = document.documentElement.getAttribute('data-theme') || 'dark';
   134→        return currentTheme === 'dark' ? darkColor : lightColor;
   135→    },
   136→
   137→    // Check if current theme is dark
   138→    isDarkTheme() {
   139→        const currentTheme = document.documentElement.getAttribute('data-theme') || 'dark';
   140→        return currentTheme === 'dark';
   141→    },
   142→
   143→    // Apply theme-specific styling to dynamic elements
   144→    applyThemeToElement(element, darkStyles = {}, lightStyles = {}) {
   145→        const isDark = this.isDarkTheme();
   146→        const styles = isDark ? darkStyles : lightStyles;
   147→        
   148→        Object.keys(styles).forEach(property => {
   149→            element.style[property] = styles[property];
   150→        });
   151→    }
   152→};
   153→
   154→// Initialize theme switcher when DOM is loaded
   155→document.addEventListener('DOMContentLoaded', function() {
   156→    window.themeSwitcher = new ThemeSwitcher();
   157→    
   158→    // Add smooth transitions to all elements
   159→    document.body.style.transition = 'background-color 0.3s ease, color 0.3s ease';
   160→    
   161→    // Initialize theme-aware components
   162→    initializeThemeAwareComponents();
   163→});
   164→
   165→// Initialize components that need theme awareness
   166→function initializeThemeAwareComponents() {
   167→    // Update chart colors if charts exist
   168→    document.addEventListener('themeChanged', function(e) {
   169→        const theme = e.detail.theme;
   170→        
   171→        // Update any existing charts or dynamic components
   172→        updateChartsTheme(theme);
   173→        updateStatusIndicators(theme);
   174→        updateToastThemes(theme);
   175→    });
   176→}
   177→
   178→// Update chart themes (for future chart implementations)
   179→function updateChartsTheme(theme) {
   180→    // Placeholder for chart theme updates
   181→    console.log(`Charts updated for ${theme} theme`);
   182→}
   183→
   184→// Update status indicators
   185→function updateStatusIndicators(theme) {
   186→    const statusElements = document.querySelectorAll('.status-indicator, .status-item');
   187→    statusElements.forEach(element => {
   188→        element.classList.add('fade-in-theme');
   189→        setTimeout(() => {
   190→            element.classList.remove('fade-in-theme');
   191→        }, 200);
   192→    });
   193→}
   194→
   195→// Update toast themes
   196→function updateToastThemes(theme) {
   197→    const toasts = document.querySelectorAll('.toast');
   198→    toasts.forEach(toast => {
   199→        // Toast styling is handled by CSS variables, this just triggers animations
   200→        toast.classList.add('fade-in-theme');
   201→        setTimeout(() => {
   202→            toast.classList.remove('fade-in-theme');
   203→        }, 200);
   204→    });
   205→}
   206→
   207→// Export for use in other modules
   208→if (typeof module !== 'undefined' && module.exports) {
   209→    module.exports = { ThemeSwitcher, ThemeUtils };
   210→}
   211→
   212→// Global theme utilities for inline scripts
   213→window.ThemeUtils = ThemeUtils;

